import React, { Component } from 'react';
import { Jumbotron, Container,Breadcrumb } from 'react-bootstrap';
import axios from 'axios';




class PostDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: []
          };
       console.log("id is here",this.props.match.params.id)
    }


    componentDidMount() {
        const { match: { params } } = this.props;

        axios.get(`https://public-api.wordpress.com/rest/v1/sites/truecaller.blog/posts/${params.userId}`)
        .then((res)=>{
            console.log('The list of posts are ',res);
            this.setState({
                posts : res.data
            })
                })
                .catch((err)=>{
            console.log('The error is ',err);
                })
      }

    render(){

      return (
       <>
<Breadcrumb>
  <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
  <Breadcrumb.Item active >
    Post Details
  </Breadcrumb.Item>

</Breadcrumb>

<div class="col-md-6 col-lg-4">
<Jumbotron fluid>
  <Container>
    <h1>ssss</h1>
    <p>
      This is a modified jumbotron that occupies the entire horizontal space of
      its parent.
    </p>
  </Container>
</Jumbotron>


    </div>

       </>
      )
    }




    }

    export default PostDetail;
