import React, { Component } from 'react';
import { Row,Col, Container,Breadcrumb } from 'react-bootstrap';
import axios from 'axios';




class PostDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
            posts: []
          };
    }


    componentDidMount() {


        axios.get(`https://public-api.wordpress.com/rest/v1/sites/truecaller.blog/posts/${this.props.match.params.id}`)
        .then((res)=>{
            console.log('The list of posts are ',res);
            this.setState({
                posts : res.data
            })
                })
                .catch((err)=>{
            console.log('The error is ',err);
                })
                console.log('The list of posts are ',this.state.posts);

      }


    render(){


      return (
       <>
<Breadcrumb>
  <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
  <Breadcrumb.Item active >
    Post Details
  </Breadcrumb.Item>

</Breadcrumb>
<Container>
    {this.state.posts.length}

{this.state.posts.map(p=>
            <li className = "partners" key={p.id}>
                <img src={p.img} alt={p.name}/> {p.name} </li>
)
    }


</Container>

       </>
      )
    }




    }

    export default PostDetail;
