import React from 'react';
import { Row, Col, Container, Button, Card, Breadcrumb } from 'react-bootstrap';
import axios from 'axios';


class PostDisplay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      postData: [],
    };
  }

  componentDidMount() {
    axios.get("https://public-api.wordpress.com/rest/v1/sites/truecaller.blog/posts").then((res) => {
      console.log('The product list is ', res.data);
      this.setState({
        postData: res.data
      })
    })
      .catch((err) => {
        console.log('The error is ', err);
      })


  }


  render() {
    {let res = this.state.postData.posts }
    return (<>


      <Breadcrumb>
        <Breadcrumb.Item href="#">Home</Breadcrumb.Item>
        <Breadcrumb.Item href="https://getbootstrap.com/docs/4.0/components/breadcrumb/">
        </Breadcrumb.Item>
      </Breadcrumb>


      <Container fluid="md">


        <Row>

          <Col><Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>


              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card></Col>


          <Col><Card style={{ width: '18rem' }}>

            <Card.Body>
              <Card.Title>Card Title</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up the bulk of
                the card's content.
    </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card></Col>
          <hr />


        </Row>

      </Container>



    </>)
  }

}
export default PostDisplay;
