import React from 'react';
import { Row, Col, Container, Button, Card, Breadcrumb } from 'react-bootstrap';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import ShowMoreText from 'react-show-more-text';

class PostDisplay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      data: [],
      perPage: 4,
      currentPage: 0,
      postData: [],
    };
    this.handlePageClick = this
      .handlePageClick
      .bind(this);
  }
  executeOnClick(isExpanded) {
    console.log(isExpanded);
  }

  displayCutString(s, n){
    var cut= s.indexOf(' ', n);
    if(cut=== -1) return s;
    return s.substring(0, cut)

  }

  removeSpecialChar(data) {
    data.replace(/(<([^>]+)>)/gi, "");
  }
  receivedData() {
    axios
      .get(`https://public-api.wordpress.com/rest/v1/sites/truecaller.blog/posts`)
      .then(res => {
        const data = res.data.posts;
        const slice = data.slice(this.state.offset, this.state.offset + this.state.perPage)

        const postData = slice.map(pd => <React.Fragment>
          <div class="col-md-6 col-lg-4">
            <Col className="card-deck"><Card style={{ width: '30rem', height: '35rem' }}>
              <Card.Img variant="top" src={pd.featured_image} className="card-picture" />
              <Card.Body>
                <Card.Title>
                  {pd.title}
                { this.displayCutString(pd.content,10)

                  }
                  </Card.Title>
                <Card.Text>

                  <ShowMoreText
                    /* Default options */
                    lines={3}
                    more='Show more'
                    less='Show less'
                    anchorClass=''

                    expanded={false}
                    width={280}
                  >
                    {pd.content}
                  </ShowMoreText>
                </Card.Text>
                <Button variant="primary">Click here</Button>
              </Card.Body>
            </Card></Col>
          </div>


        </React.Fragment>)

        this.setState({
          pageCount: Math.ceil(data.length / this.state.perPage),

          postData
        })
      });
  }
  handlePageClick = (e) => {
    const selectedPage = e.selected;
    const offset = selectedPage * this.state.perPage;

    this.setState({
      currentPage: selectedPage,
      offset: offset
    }, () => {
      this.receivedData()
    });

  };

  componentDidMount() {
    this.receivedData()
  }
  render() {

    return (
      <div>
        <Breadcrumb>
          <Breadcrumb.Item href="#">Home</Breadcrumb.Item>

        </Breadcrumb>


        <Container fluid="md">
          <Row>

            {this.state.postData}

          </Row>
          <Row>
            {this.state.postData.length > 0 ? (
              <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={this.state.pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={this.handlePageClick}
                containerClassName={"pagination"}
                subContainerClassName={"pages pagination"}
                activeClassName={"active"} />

            ) : null


            }

          </Row>

        </Container>


      </div>

    )
  }

}
export default PostDisplay;
